        <MDBBtn gradient="aqua" block="true" type="submit" className="mr-auto"  ><h2>Mall Map </h2></MDBBtn>  
       </div>
       <div className="conterner"> 
          {( e =><Brands  ele ={e}  stors = {this.state.items} changeSelecMall={this.props.changeSelecMall}/>)}  
